["custom"] = "function(states, event, keybind, arg)\n    local cfg = aura_env.config\n    if event == \"ACHIEVEMENT_EARNED\" and keybind == 41038 then\n        aura_env.collector = select(3, GetAchievementCriteriaInfo(41038, 1)) --double check lol\n    end\n    \n    if cfg.mod and event == \"MODIFIER_STATE_CHANGED\" and keybind == aura_env.key and arg == 1 then\n        aura_env.show = not aura_env.show\n        if not aura_env.show then\n            for _, state in pairs(states) do\n                state.show = false\n                state.changed = true\n            end\n            return true\n        end\n    end\n    \n    if aura_env.show and cfg.copy\n    and event == \"GLOBAL_MOUSE_DOWN\" and keybind and not InCombatLockdown()\n    then\n        local button = keybind\n        if (button == \"LeftButton\" or button == \"RightButton\") and IsLeftControlKeyDown() then\n            local questID\n            for _, v in pairs(aura_env.regions) do\n                local over = MouseIsOver(v)\n                if over then\n                    questID = v.state and v.state.tooltip\n                    break\n                end\n            end\n            if questID then\n                StaticPopup_Show(\"TRANSLIT_20TH\", questID, C_QuestLog.GetTitleForQuestID(questID))\n            end\n        end\n    elseif event == \"OPTIONS\"\n    or (not cfg.mod and event ~= \"MODIFIER_STATE_CHANGED\")\n    or (cfg.mod and aura_env.show and (event == \"MODIFIER_STATE_CHANGED\" and arg == 1 or event ~= \"MODIFIER_STATE_CHANGED\")) then \n        local count = 1\n        for i, data in ipairs(aura_env.data) do\n            if data.enabled then\n                local npc = data.npc\n                states[npc] = {\n                    hub = i,\n                    index = count,\n                    title = npc,\n                    changed = true,\n                    show = true,\n                }\n                count = count + 1\n                \n                local questID, questAtlas, showNpc, progress\n                for _, questInfo in ipairs(data.quests) do\n                    progress, questAtlas = questInfo[1], questInfo[2]\n                    if not progress then\n                        local title = questInfo[3]\n                        local completed = aura_env.instance()\n                        local show = not cfg.hide or cfg.hide and not completed\n                        showNpc = showNpc or show\n                        \n                        states[title] = {\n                            hub = i,\n                            index = count,\n                            title = format(\"%s %s\", questAtlas, title),\n                            completed = completed,\n                            changed = true,\n                            show = show,\n                        }\n                        count = count + 1\n                    elseif type(progress) == \"table\" then\n                        local completed, completedID, IsOnQuest, IsOnQuestID\n                        for _, id in ipairs(progress) do\n                            completed = completed or C_QuestLog.IsQuestFlaggedCompletedOnAccount(id)\n                            if completed and not completedID then\n                                completedID = id\n                            end  \n                            IsOnQuest = IsOnQuest or C_QuestLog.IsOnQuest(id)\n                            if IsOnQuest and not IsOnQuestID then\n                                IsOnQuestID = id\n                            end  \n                        end\n                        \n                        questID = IsOnQuestID or completedID\n                        local title = questID and C_QuestLog.GetTitleForQuestID(questID) or questInfo[3]\n                        local show = not cfg.hide or cfg.hide and not completed\n                        showNpc = showNpc or show\n                        \n                        states[title] = {\n                            hub = i,\n                            index = count,\n                            title = format(\"%s %s\", questAtlas, title),\n                            completed = completed,\n                            changed = true,\n                            show = show,\n                            tooltip = questID,\n                        }\n                        count = count + 1\n                        \n                        for _, state in pairs(states) do --we don't have questID after removed (IsOnQuestID = nil)\n                            if state.questID and state.index > count then --do not remove steps for previous quests\n                                state.changed = true\n                                state.show = false\n                            end\n                        end\n                        \n                        local questData = IsOnQuestID and C_QuestLog.GetQuestObjectives(IsOnQuestID)\n                        if questData and questData[1] then\n                            for j, v in ipairs(questData) do\n                                local key = IsOnQuestID..j\n                                states[key] = {\n                                    hub = i,\n                                    questID = IsOnQuestID,\n                                    index = count,\n                                    step = \"• \",\n                                    IsOnQuest = IsOnQuest,\n                                    text = v.text,\n                                    finished = v.finished,\n                                    fulfilled = v.numFulfilled,\n                                    required = v.numRequired,\n                                    changed = true,\n                                    show = show,\n                                }\n                                count = count + 1\n                            end\n                        end\n                    else\n                        questID = progress\n                        local completed = C_QuestLog.IsQuestFlaggedCompletedOnAccount(questID)\n                        if questID == 84616 and not completed then --Celebrate Good Fun! workaround\n                            completed = not aura_env.getReward(questID)\n                        end\n                        \n                        local show = not cfg.hide or cfg.hide and not completed\n                        if questInfo[4] and completed then --hide completed/not awarding PvP quests (until realizing how to check for current weekly rotation)\n                            show = false\n                        end\n                        showNpc = showNpc or show\n                        \n                        local title = C_QuestLog.GetTitleForQuestID(questID) or questID\n                        states[title] = {\n                            hub = i,\n                            index = count,\n                            title = format(\"%s %s\", questAtlas, title),\n                            completed = completed,\n                            changed = true,\n                            show = show,\n                            tooltip = questID,\n                        }\n                        count = count + 1\n                        --[[\n                        for _, state in pairs(states) do\n                            if state.questID and state.questID == questID then\n                                state.changed = true\n                                state.show = false\n                            end\n                        end\n                        --]]\n                        local IsOnQuest = C_QuestLog.IsOnQuest(questID)\n                        local questData = C_QuestLog.GetQuestObjectives(questID)\n                        if questData and questData[1] then\n                            for j, v in ipairs(questData) do\n                                local key = questID..j\n                                if states[key] and not IsOnQuest then\n                                    states[key].changed = true\n                                    states[key].show = false \n                                elseif IsOnQuest then\n                                    states[key] = {\n                                        hub = i,\n                                        questID = questID,\n                                        index = count,\n                                        step = \"• \",\n                                        IsOnQuest = IsOnQuest,\n                                        text = v.text,\n                                        finished = v.finished,\n                                        fulfilled = v.numFulfilled,\n                                        required = v.numRequired,\n                                        changed = true,\n                                        show = show,\n                                    }\n                                    count = count + 1\n                                end\n                            end\n                        end\n                    end\n                end\n                count = count + 50\n                if not showNpc and states[npc] then\n                    states[npc].show = false\n                    states[npc].changed = true\n                end\n            end\n        end\n        return true\n    end\nend",
