["custom"] = "function(event, arg1)\n    if (event == \"PLAYER_EQUIPMENT_CHANGED\") then\n        -- This event is fired when an equipment slot is changed\n        \n        if arg1 == nil then\n            return true\n        end\n        \n        aura_env.updateSlot(\"player\", arg1)\n        return true\n    elseif (event == \"UNIT_INVENTORY_CHANGED\") then\n        -- Supposedly fired when inspect target inventory changes\n        \n        if arg1 == nil then\n            return true\n        end\n        \n        aura_env.updateAllSlots(arg1)\n        return true\n    elseif (event == \"INSPECT_READY\") then\n        -- Fired, possibly multiple times, when inspect data is ready\n        \n        if arg1 == nil then\n            return true\n        end\n        \n        if arg1 == aura_env.lastInspectGuid then\n            aura_env.updateAllSlots(aura_env.lastInspectUnit)\n        end\n        return true\n    elseif ((event == \"BAG_UPDATE_DELAYED\" and aura_env.initialScan) or event == \"STATUS\") then\n        -- Scan all item slots\n        aura_env.initialScan = false\n        aura_env.updateAllSlots(\"player\")\n        return true\n    end\nend\n\n-- BAG_UPDATE_DELAYED, PLAYER_EQUIPMENT_CHANGED, INSPECT_READY, UNIT_INVENTORY_CHANGED",
