["custom"] = "local aura_env = aura_env\nlocal _G = _G\n\naura_env.initialScan = true\n\n-- Hooking NotifyInspect to remember the unit last inspected\n-- This will be used async when INSPECT_READY is called\naura_env.lastInspectUnit = nil\naura_env.lastInspectGuid = nil\n\n-- For adding average ilvl to inspect frame\nlocal inspectAverage = 0\n\nhooksecurefunc(\"NotifyInspect\", function(unit)\n        -- there's some weird thing where inspect is called on yourself? Ignore these\n        if (unit == GetUnitName(\"player\")) then return end\n        \n        aura_env.lastInspectUnit = unit\n        aura_env.lastInspectGuid = UnitGUID(unit)\nend)\n\n-- Get the name of the item frame\nlocal getSlotFrameName = function(unit, slot)\n    local slotName\n    local prefix = \"Inspect\"\n    if (unit == \"player\") then\n        prefix = \"Character\"\n    end\n    \n    if slot == 1 then slotName = \"Head\"\n    elseif (slot == 2) then slotName = \"Neck\"\n    elseif (slot == 3) then slotName = \"Shoulder\"\n    elseif (slot == 4) then slotName = \"Shirt\"\n    elseif (slot == 5) then slotName = \"Chest\"\n    elseif (slot == 6) then slotName = \"Waist\"\n    elseif (slot == 7) then slotName = \"Legs\"\n    elseif (slot == 8) then slotName = \"Feet\"\n    elseif (slot == 9) then slotName = \"Wrist\"\n    elseif (slot == 10) then slotName = \"Hands\"\n    elseif (slot == 11) then slotName = \"Finger0\"\n    elseif (slot == 12) then slotName = \"Finger1\"\n    elseif (slot == 13) then slotName = \"Trinket0\"\n    elseif (slot == 14) then slotName = \"Trinket1\"\n    elseif (slot == 15) then slotName = \"Back\"\n    elseif (slot == 16) then slotName = \"MainHand\"\n    elseif (slot == 17) then slotName = \"SecondaryHand\"\n    elseif (slot == 19) then slotName = \"Tabard\"\n    else return nil\n    end\n    \n    return prefix .. slotName .. \"Slot\"\nend\n\n-- Returns true if the slot is on the right side of the character panel\nlocal isRightSide = function(slot)\n    if (slot == 6 or slot == 7 or slot == 8 or slot == 10 or slot == 11 or slot == 12 or slot == 13 or slot == 14 or\n    slot == 16) then\n        return true\n    end\n    return false\nend\n\nlocal fontMap = {\n    nil, -- none\n    \"OUTLINE\",\n    \"THICKOUTLINE\",\n    nil -- drop shadow\n}\n\nlocal getRarityColor = function(level) \n    if (level < 250) then return \"FFFFFFFF\" end\n    if (level < 340) then return \"FF1EFF00\" end\n    if (level < 370) then return \"FF0070DD\" end\n    if (level < 420) then return \"FFA335EE\" end\n    return \"FFFF8000\"\nend\n\n-- Update a Specific Slot\naura_env.updateSlot = function(unit, slot)\n    if (unit == nil or slot == nil) then\n        return\n    end\n    \n    local itemLink = GetInventoryItemLink(unit, slot)\n    local slotFrameName = getSlotFrameName(unit, slot)\n    if (slotFrameName == nil or _G[slotFrameName] == nil) then return end\n    \n    local rightSide = isRightSide(slot)\n    local framePoint = rightSide and \"RIGHT\" or \"LEFT\"\n    local parentPoint = rightSide and \"LEFT\" or \"RIGHT\"\n    local offsetX = rightSide and -10 or 9\n    \n    local LevelText = _G[slotFrameName .. \"TyIlvl\"]\n    local AverageLevelText = _G[\"TyAvgIlvl\"]\n    local EnchantText = _G[slotFrameName .. \"TyEnchant\"]\n    local GemFrames = {}\n    for i = 1, 3 do\n        GemFrames[i] = _G[slotFrameName .. \"TyGem\" .. i]\n    end\n    \n    -- create and position frames if they don't exist\n    if (LevelText == nil) then\n        LevelText = _G[slotFrameName]:CreateFontString(slotFrameName .. \"TyIlvl\", \"ARTWORK\", \"GameTooltipText\")\n        if (slot == 16 or slot == 17) then -- weapons put the ilvl on top\n            LevelText:SetPoint(\"BOTTOM\", _G[slotFrameName], \"TOP\", 0, 5)\n        else\n            LevelText:SetPoint(framePoint, _G[slotFrameName], parentPoint, offsetX, 0)\n        end\n    end\n    if (aura_env.config.items.fontOutline == 4) then\n        LevelText:SetShadowColor(0, 0, 0)\n        LevelText:SetShadowOffset(1, -1)\n    end\n    local font = LevelText:GetFont()\n    LevelText:SetFont(font, aura_env.config.items.fontSize, fontMap[aura_env.config.items.fontOutline])\n    \n    if (_G[\"InspectModelFrame\"] ~= nil) then\n        if (AverageLevelText == nil) then\n            AverageLevelText = _G[\"InspectModelFrame\"]:CreateFontString(\"TyAvgIlvl\", \"OVERLAY\", \"GameTooltipText\")\n        end\n        \n        if (aura_env.config.items.showAverage) then\n            AverageLevelText:SetPoint(\"TOP\", _G[\"InspectModelFrame\"], \"TOP\", 0, -5)\n            \n            local font = AverageLevelText:GetFont()\n            AverageLevelText:SetFont(font, aura_env.config.items.fontSize, fontMap[aura_env.config.items.fontOutline])\n            \n            local averageLevel = C_PaperDollInfo.GetInspectItemLevel(unit)\n            local rarityColor = getRarityColor(averageLevel)\n            AverageLevelText:SetText(\"|c\" .. rarityColor .. averageLevel .. \"|r\")\n            AverageLevelText:Show()\n        else\n            AverageLevelText:SetText(\"\")\n            AverageLevelText:Hide()\n        end\n    end\n    \n    if (EnchantText == nil) then\n        EnchantText = _G[slotFrameName]:CreateFontString(slotFrameName .. \"TyEnchant\", \"ARTWORK\", \"GameTooltipText\")\n        EnchantText:SetPoint(framePoint, _G[slotFrameName], parentPoint, offsetX, -12)\n    end\n    if (aura_env.config.items.fontOutline == 4) then\n        EnchantText:SetShadowColor(0, 0, 0)\n        EnchantText:SetShadowOffset(1, -1)\n    end\n    local font, _ = EnchantText:GetFont()\n    EnchantText:SetFont(font, aura_env.config.enchants.fontSize, fontMap[aura_env.config.enchants.fontOutline])\n    \n    -- set up gems\n    for i = 1, 3 do\n        if (GemFrames[i] == nil) then\n            GemFrames[i] = CreateFrame(\"Button\", slotFrameName .. \"TyGem\" .. i, _G[slotFrameName],\n            \"UIPanelButtonTemplate\")\n            GemFrames[i]:SetSize(14, 14)\n        end\n        if (slot == 16 or slot == 17) then\n            GemFrames[i]:SetPoint(\"BOTTOM\", _G[slotFrameName], \"TOP\", -14 + (15 * (i - 1)), 18)\n        else\n            local gemOffsetX = rightSide and offsetX - (15 * (i - 1)) or offsetX + (15 * (i - 1))\n            if (aura_env.config.items.enabled) then\n                gemOffsetX = rightSide and gemOffsetX - 27 or gemOffsetX + 27\n            end\n            GemFrames[i]:SetPoint(framePoint, _G[slotFrameName], parentPoint, gemOffsetX, 0)\n        end\n    end\n    \n    -- clear all if no item equipped\n    if (itemLink == nil or itemLink == \"\") then\n        LevelText:SetText(\"\")\n        EnchantText:SetText(\"\")\n        for i = 1, 3 do\n            GemFrames[i]:Hide()\n        end\n        return\n    end\n    \n    -- get item information\n    local _, _, _, itemLevel = GetItemInfo(itemLink)\n    if (itemLevel == nil) then return end\n    \n    -- need to parse tooltip for full item info\n    local ItemTooltip = _G[\"TyScanningTooltip\"] or\n    CreateFrame(\"GameTooltip\", \"TyScanningTooltip\", WorldFrame, \"GameTooltipTemplate\")\n    ItemTooltip:SetOwner(WorldFrame, \"ANCHOR_NONE\");\n    ItemTooltip:ClearLines()\n    ItemTooltip:SetHyperlink(itemLink)\n    local enchant = \"\"\n    local emptySockets = 0\n    for i = 1, ItemTooltip:NumLines() do\n        local foundEnchant = _G[\"TyScanningTooltipTextLeft\" .. i]:GetText():match(ENCHANTED_TOOLTIP_LINE:gsub(\"%%s\",\n        \"(.+)\"))\n        if foundEnchant then\n            enchant = foundEnchant\n        end\n        \n        local foundLevel = _G[\"TyScanningTooltipTextLeft\" .. i]:GetText():match(ITEM_LEVEL:gsub(\"%%d\", \"(%%d+)\"))\n        if foundLevel then\n            itemLevel = foundLevel\n        end\n        \n        local foundEmptySocket = _G[\"TyScanningTooltipTextLeft\" .. i]:GetText():match(EMPTY_SOCKET_PRISMATIC)\n        if foundEmptySocket then\n            emptySockets = emptySockets + 1\n        end\n    end\n    \n    -- set iLvl\n    if (aura_env.config.items.enabled) then\n        local rarityColor = string.sub(itemLink, 3, 10)\n        LevelText:SetText(\"|c\" .. rarityColor .. itemLevel .. \"|r\")\n        LevelText:Show()\n    else\n        LevelText:Hide()\n    end\n    \n    -- set enchant\n    if (aura_env.config.enchants.enabled) then\n        -- need to check for quality symbols\n        local qualityStart = string.find(enchant, \"|\")\n        local quality = \"\"\n        if (qualityStart) then\n            quality = string.sub(enchant, qualityStart)\n            enchant = string.sub(enchant, 1, qualityStart - 1)\n        end\n        \n        local maxLength = aura_env.config.enchants.maxLength\n        if (maxLength > 0 and strlen(enchant) > maxLength) then\n            enchant = format(\"%.\" .. maxLength .. \"s\", enchant) .. \"...\"\n        end\n        if (aura_env.config.enchants.showQuality) then\n            enchant = enchant .. quality;\n        end\n        EnchantText:SetText(\"|cFF00FF00\" .. enchant .. \"|r\")\n        EnchantText:Show()\n    else\n        EnchantText:Hide()\n    end\n    \n    -- set gems\n    local gemLinks = {}\n    for i = 1, 3 do\n        if (aura_env.config.gems.enabled) then\n            _, gemLinks[i] = GetItemGem(itemLink, i)\n            if (gemLinks[i] ~= nil) then\n                local gemIcon = GetItemIcon(gemLinks[i])\n                GemFrames[i]:SetNormalTexture(gemIcon)\n                GemFrames[i]:Show()\n                \n                GemFrames[i]:SetScript(\"OnEnter\", function()\n                        GameTooltip:SetOwner(GemFrames[i], \"ANCHOR_CURSOR\")\n                        GameTooltip:SetHyperlink(gemLinks[i])\n                        GameTooltip:Show()\n                end)\n                GemFrames[i]:SetScript(\"OnLeave\", function()\n                        GameTooltip:Hide()\n                end)\n            elseif (emptySockets > 0) then\n                GemFrames[i]:SetNormalTexture(\"Interface\\\\ITEMSOCKETINGFRAME\\\\UI-EmptySocket-Prismatic.blp\")\n                GemFrames[i]:Show()\n                emptySockets = emptySockets - 1\n            else\n                GemFrames[i]:Hide()\n            end\n        else\n            GemFrames[i]:Hide()\n        end\n    end\nend\n\n-- loop all slots\naura_env.updateAllSlots = function(unit)\n    for slot = 1, 19 do\n        aura_env.updateSlot(unit, slot)\n    end\nend",
