\n        [\"outlineInFront\"] = false,\n        [\"showLabel\"] = false,\n        [\"textColor\"] = {1,1,1},\n    },\n    [\"TaxiNode_Neutral\"] = { -- Flightpoint \n        [\"desaturate\"] = false,\n        [\"color\"] = {1,1,1,1},\n        [\"outline\"] = true,\n        [\"outlineScale\"] = 1,\n        [\"outlineDesaturate\"] = false,\n        [\"outlineColor\"] = {1,1,1,0.3},\n        [\"outlineBlendMode\"] = \"ADD\",\n        [\"outlineInFront\"] = true,\n        [\"showLabel\"] = false,\n        [\"textColor\"] = {1,1,1},\n    },\n    [\"TaxiNode_Undiscovered\"] = { -- Flightpoint undiscovered\n        [\"desaturate\"] = false,\n        [\"color\"] = {1,1,1,1},\n        [\"outline\"] = true,\n        [\"outlineScale\"] = 1,\n        [\"outlineDesaturate\"] = false,\n        [\"outlineColor\"] = {1,1,1,0.3},\n        [\"outlineBlendMode\"] = \"ADD\",\n        [\"outlineInFront\"] = true,\n        [\"showLabel\"] = false,\n        [\"textColor\"] = {1,1,1},\n    },\n    [\"poi-hub\"] = { -- town icon\n        [\"desaturate\"] = false,\n        [\"color\"] = {1,1,1,1},\n        [\"outline\"] = true,\n        [\"outlineScale\"] = 1,\n        [\"outlineDesaturate\"] = true,\n        [\"outlineColor\"] = {1,1,1,0.75},\n        [\"outlineBlendMode\"] = \"ADD\",\n        [\"outlineInFront\"] = false,\n        [\"showLabel\"] = false,\n        [\"textColor\"] = {1,1,1},\n    },\n    \n}\n\nif not _G.THV_MapIsSet then\n    WorldMapFrame:HookScript(\"OnShow\", function() WeakAuras.ScanEvents(\"THV_UpdateMap\") end)\n    setglobal(\"THV_MapIsSet\", true)\nend\n\naura_env.ApplySettings = function(frame, atlasName)\n    local info = aura_env.atlasLookup[atlasName]\n    \n    -- hiding if not needed\n    if not info then\n        if frame.THV_background then\n            frame.THV_background:Hide()\n        end\n        \n        if frame.THV_label then\n            frame.THV_label:Hide()\n        end\n        \n        return\n    end\n    \n    -- coloring original icon\n    if info.desaturate then\n        frame.Texture:SetDesaturated(true)\n    else\n        frame.Texture:SetDesaturated(false)\n    end\n    frame.Texture:SetVertexColor(unpack(info.color))\n    \n    -- glow texture\n    if info.outline then\n        if not frame.THV_background then\n            frame.THV_background = frame:CreateTexture()\n        end\n        \n        local w,h = frame.Texture:GetSize()\n        local layer, sublevel = frame.Texture:GetDrawLayer()\n        local mult = 1\n        if info.outlineInFront then mult = -1 end\n        frame.THV_background:SetAtlas(atlasName)\n        frame.THV_background:SetDrawLayer(layer, sublevel-(1*mult))\n        frame.THV_background:SetSize(w, h)\n        frame.THV_background:SetPoint(\"CENTER\", frame, \"CENTER\", 0, 0)\n        frame.THV_background:SetVertexColor(unpack(info.outlineColor))\n        frame.THV_background:SetScale(info.outlineScale)\n        frame.THV_background:SetBlendMode(info.outlineBlendMode)\n        \n        if info.outlineDesaturate then\n            frame.THV_background:SetDesaturated(true)\n        else\n            frame.THV_background:SetDesaturated(false)\n        end\n        \n        frame.THV_background:Show()\n    elseif frame.THV_background then\n        frame.THV_background:Hide()\n    end\n    \n    -- labels\n    if info.showLabel and aura_env.config.showLabels then\n        if not frame.THV_label then\n            local f = CreateFrame(\"Frame\", \"THV_labelBase\", WorldMapFrame)\n            local t = f:CreateFontString()\n            frame.THV_label = t\n            f:SetParent(WorldMapFrame)\n            f:SetFrameStrata(\"HIGH\") \n            t:SetFont(GameFontNormal:GetFont(), 9, \"OUTLINE\")\n            t:SetPoint(\"BOTTOM\", frame, \"TOP\", 0, 2)\n        end\n        frame.THV_label:Show()\n        frame.THV_label:SetText(frame.poiInfo.name)\n        frame.THV_label:SetVertexColor(unpack(info.textColor))\n    elseif frame.THV_label then\n        frame.THV_label:Hide()\n    end\n    \nend\n\n\n",
